#include <stdio.h>

double celsius(double fahrenheit);
double fahrenheit(double celsius);

int main(){
	printf("The following is celsius 0 - 100 in fahrenheit:\n\n");
	for(double i = 0; i <= 100; i++){
		printf("%.2f C = %.2f F\t", i, fahrenheit(i));
	}

	printf("\n\nThe following is fahrenheit 32 - 212 in celsius:\n\n");

	for(double i = 32; i <= 212; i++){
		printf("%.2f F = %.2f C\t", i, celsius(i));
	}

	printf("\n");

	return 0;
}

double celsius(double fahrenheit){
	return(fahrenheit / 1.8 - 32);
}

double fahrenheit(double celsius){
	return (celsius * 1.8 + 32);
}

